import { EventEmitter, Injectable } from "@angular/core";
import { Ingredient } from "../shared/Ingredient";
import { Recipe } from "./recipes/recipe.model";

@Injectable({providedIn: 'root'})
export class RecipeService
{
    public readonly recipeSelected = new EventEmitter<Recipe>();

    private readonly recipes: Recipe[] = [
        new Recipe(
          "Dahl", 
          "Linser og alt andet bliver bare smadret sammen på samme tid.", 
          "data:image/jpeg;base64,",
          [
            new Ingredient('Røde Linser', 300),
            new Ingredient('Kokosmælk', 400)
          ]),
        new Recipe("Linsesovs", "En omgang sovsede linser", "data:image/jpeg;base64,",
        [
          new Ingredient('Røde Linser', 300),
          new Ingredient('Kokosmælk', 400),
          new Ingredient('Vand', 600)
        ])
    ];

    public get recipeList() { return this.recipes.slice(); }
}